import React from "react";
import styled from "styled-components";

const Input = styled.div`
  padding: 10px;
  margin: 20px;

  .textfield {
    position: relative;
  }

  .textfield > input {
    border: none;
    padding: 16px 14px;
  }
  .textfield > input:focus {
    outline: none;
  }

  span {
    position: absolute;
    padding-left: 5px;
    padding-right: 5px;
    font-size: 12px;
    color: #007fff;
    opacity: 0;
  }

  /* outline */
  .textfield_outlined > input {
    border: 1px solid #666;
    border-radius: 5px;
  }
  .textfield_outlined > input:focus {
    box-sizing: unset;
    border: 2px solid #007fff;
  }
  .textfield_outlined > span {
    top: -8px;
    left: 10px;
    background-color: #fff;
  }

  /* standard */
  .textfield_standard > input {
    border-bottom: 1px solid #666;
  }
  .textfield_standard > input:focus {
    border-bottom: 2px solid #007fff;
  }
  .textfield_standard > span {
    top: -5px;
    left: 8px;
  }

  /* filled */
  .textfield_filled > input {
    border-bottom: 1px solid #666;
    background-color: #f7f7f7;
  }
  .textfield_filled > input:focus {
    border-bottom: 2px solid #007fff;
  }
  .textfield_filled > span {
    top: -5px;
    left: 8px;
  }
`;

const Textfield = ({ type, text }) => {
  const onFocus = () => {
    console.log("hu");
  };
  return (
    <Input>
      <div className={["textfield", `textfield_${type}`].join(" ")}>
        <input placeholder={text} onFocus={onFocus} />
        <span>{text}</span>
      </div>
    </Input>
  );
};

export default Textfield;
