import React from "react";
import styled from "styled-components";

const Input = styled.div`
  margin: 20px;
  .textfield {
    border: none;
    padding: 16px 14px;
  }
  .textfield:focus {
    outline: none;
  }

  .textfield_outlined {
    border: 1px solid #666;
    border-radius: 5px;
  }
  .textfield_outlined:focus {
    outline: 1px solid #007fff;
  }

  .textfield_standard {
    border-bottom: 1px solid #666;
  }
  .textfield_standard:focus {
    border-bottom: 1px solid #007fff;
  }

  .textfield_filled {
    border-bottom: 1px solid #666;
    background-color: #f7f7f7;
  }
`;

const Textfield = ({ type, text }) => {
  return (
    <div>
      <Input>
        <input
          className={["textfield", `textfield_${type}`].join(" ")}
          placeholder={text}
        ></input>
      </Input>
    </div>
  );
};

export default Textfield;
