import React from "react";
import styled from "styled-components";

const Input = styled.div`
  .Textfield {
    /* position: relative; */
    padding: 10px;
    margin: 20px;
  }

  .Textfield:focus-within {
  }
  .textfield {
    border: none;
    padding: 16px 14px;
  }
  .textfield:focus {
    outline: none;
  }

  span {
    position: absolute;
    background-color: #fff;
    padding-left: 5px;
    padding-right: 5px;
    font-size: 12px;
    color: #007fff;
    top: 0;
    left: 20px;
  }

  .textfield_outlined {
    border: 1px solid #666;
    border-radius: 5px;
  }
  .textfield_outlined:focus {
    border: 2px solid #007fff;
  }

  .textfield_standard {
    border-bottom: 1px solid #666;
  }
  .textfield_standard:focus {
    border-bottom: 2px solid #007fff;
  }

  .textfield_filled {
    border-bottom: 1px solid #666;
    background-color: #f7f7f7;
  }
  .textfield_filled:focus {
    border-bottom: 2px solid #007fff;
  }
`;

const Textfield = ({ type, text }) => {
  return (
    <Input>
      <div className="Textfield">
        <input
          className={["textfield", `textfield_${type}`].join(" ")}
          placeholder={text}
        />
        <span>{text}</span>
      </div>
    </Input>
  );
};

export default Textfield;
